import React, { Component } from "react";
import { Link } from "react-router-dom";

class Featured extends Component {
  render() {
    const data = this.props.article;

    function checkAndRenderName() {
      if (typeof data === "undefined") {
        return;
      } else {
        return data.name;
      }
    }
    function checkAndRenderAuthor() {
      if (typeof data === "undefined") {
        return;
      } else {
        return data.author;
      }
    }
    function checkAndRenderPreview() {
      if (typeof data === "undefined") {
        return;
      } else {
        return data.preview;
      }
    }

    function checkAndRenderId() {
      if (typeof data === "undefined") {
        return;
      } else {
        return "/article/" + data._id;
      }
    }

    return (
      <div id="featured">
        <div id="featured-title">Featured article</div>
        <Link id="featured-name" to={checkAndRenderId()}>
          {checkAndRenderName()}
        </Link>
        <div id="featured-author">by {checkAndRenderAuthor()}</div>
        <div id="featured-preview">{checkAndRenderPreview()}</div>
      </div>
    );
  }
}
export default Featured;
