import { GET_AUTHOR } from "../actions/types";

const intialState = {
  author: [],
  loading: false,
};

export default function (state = intialState, action) {
  switch (action.type) {
    case GET_AUTHOR:
      console.log("get author");
      console.log(action.payload);

      return {
        ...state,

        author: action.payload,
        loading: false,
      };
    // case AUTHORS_LOADING:
    //   return {
    //     ...state,
    //     loading: true,
    //   };

    default:
      return state;
  }
}
