import React, { Component } from "react";

import { connect } from "react-redux";

class AuthorBlerb extends Component {
  render() {
    // const imagePath = `../../../public/authorImages/${this.props.author.image}`;

    // function checkAndRenderimage() {
    //   if (typeof this.props.author === "undefined") {
    //     return;
    //   } else {
    //     return `../../../public/authorImages/${this.props.author.image}`;
    //   }
    // }
    console.log(typeof this.props.author.image);

    const image = this.props.author.image;

    function checkAndRenderimage() {
      if (typeof image === "undefined") {
        return;
      } else {
        return `../../../public/authorImages/${image}`;
      }
    }

    // const image = "robsmile.jpg";

    return (
      <div class="author-blerb">
        <div class="blerb-txt">{this.props.author.description}</div>
        {/* <img
          class="blerb-photo"
          src={require(`../../../public/authorImages/${checkAndRenderimage}`)}
        /> */}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  author: state.author.author,
});

export default connect(mapStateToProps, {})(AuthorBlerb);
