import uuid from "uuid";
import { GET_ARTICLES, ADD_ARTICLE, DELETE_ARTICLE } from "../actions/types";
// import { bindActionCreators } from "redux";

const intialState = {
  articles: [
    { id: uuid(), name: "Eggs" },
    { id: uuid(), name: "Milk" },
    { id: uuid(), name: "Steak" },
    { id: uuid(), name: "Water" },
  ],
};

export default function (state = initialState) {
  switch (action.type) {
    case GET_ARTICLES:
      return {
        ...state,
      };
    default:
      return state;
  }
}
