import React, { Component } from "react";
import { Container } from "reactstrap";

import { connect } from "react-redux";
import { getArticles, getArticle } from "../actions/articleActions";
import PropTypes from "prop-types";

class Article extends Component {
  componentDidMount() {
    this.props.getArticles();
  }

  render() {
    // const { article } = this.props;
    const { article } = this.props;
    // console.log("Article:");
    console.log(this.props);
    console.log(this.props.article);
    console.log(this.props.article.articles);

    return (
      <Container>
        <ListGroup>
          <TransitionGroup className="article-list">
            {articles.map(({ _id, name, author, body }) => (
              <ListGroupItem>
                {name}
                <br />
                {author}
                <br />
                {body.split("\r").map((c) => {
                  return <p> {c} </p>;
                })}
                <br />
              </ListGroupItem>
            ))}
          </TransitionGroup>
        </ListGroup>
        <h1>Fuck</h1>
        <Article id={0} />
      </Container>
    );
  }
}

Article.propTypes = {
  getArticles: PropTypes.func.isRequired,
  article: PropTypes.object.isRequired,
};

const mapStateToProps = (state, ownProps) => ({
  articleId: ownProps,
  article: state.article,
  // article: state.article.articles[ownProps.id],
});

export default connect(mapStateToProps, { getArticles })(Article);
