import React, { Component } from "react";
import { Container } from "reactstrap";

import { connect } from "react-redux";
import { getAuthor } from "../actions/authorActions";
import PropTypes from "prop-types";

class ArticleShow extends Component {
  componentDidMount() {
    // this.props.getArticle(this.props.match.params.id);
  }

  render() {
    return (
      <Container>
        <p>{checkAndRenderName()}</p>
        <p>{checkAndRenderAuthor()}</p>
        <p>{checkAndRenderBody()}</p>
        <p>{checkAndRenderID()}</p>
        <br />
      </Container>
    );
  }
}

ArticleShow.propTypes = {
  getAuthor: PropTypes.func.isRequired,
  author: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => {
  console.log("My store state ===>", state); // log the `state` and see the exact path for articles
  return {
    author: state.article,
  };
};

export default connect(mapStateToProps, { getAuthor })(AuthorBlerb);
