import React, { Component } from "react";
import { Container } from "reactstrap";

import { connect } from "react-redux";
import { getArticle } from "../actions/articleActions";
import PropTypes from "prop-types";

class ArticleShow extends Component {
  componentDidMount() {
    this.props.getArticle(this.props.id);
  }

  render() {
    //  console.log(this.props.article.article.Article_2);
    const data = this.props.article.article.Article_2;
    console.log(data);
    if (this.props.article.loading) {
      return <p>Loading...</p>;
    }

    return (
      <Container>
        {/* {this.props.article.article.Article_2} */}
        {/* {article.name}
        <br />
        {article.author}
        <br />
        {article.body.split("\r").map((c) => {
          return <p> {c} </p>;
        })} */}
        <br />
      </Container>
    );
  }
}

ArticleShow.propTypes = {
  getArticle: PropTypes.func.isRequired,
  article: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  article: state.article,
});

export default connect(mapStateToProps, { getArticle })(ArticleShow);
