import React, { Component } from "react";

class Featured extends Component {
  render() {
    const data = this.props.article;
    function checkAndRenderName() {
      if (typeof data === "undefined") {
        return;
      } else {
        return data.name;
      }
    }
    function checkAndRenderAuthor() {
      if (typeof data === "undefined") {
        return;
      } else {
        return data.author;
      }
    }
    function checkAndRenderPreview() {
      if (typeof data === "undefined") {
        return;
      } else {
        return data.preview;
      }
    }

    return (
      <div>
        <h2>{checkAndRenderName()}</h2>
        <h2>{checkAndRenderAuthor()}</h2>
        <h2>{checkAndRenderPreview()}</h2>
      </div>
    );
  }
}
export default Featured;
