import {
  GET_ARTICLES,
  ADD_ARTICLE,
  DELETE_ARTICLE,
  ARTICLES_LOADING,
} from "./types";

export const getArticles = () => {
  return {
    type: GET_ARTICLES,
  };
};
export const deleteArticle = (id) => {
  return {
    type: DELETE_ARTICLE,
    payload: id,
  };
};
export const addArticle = (article) => {
  return {
    type: ADD_ARTICLE,
    payload: article,
  };
};
export const setArticlesLoading = () => {
  return {
    type: ARTICLES_LOADING,
  };
};
